import React, { Component } from 'react';

import '../css/cssPageCadastro.css';

import { ContextoUsuario } from '../components/Session';
import api from '../services/api';

export default class PageCadastro extends Component {

    static contextType = ContextoUsuario;

    state = {
        username: '',
    };

    cadastrar = () => {

        if(this.possuiCamposVazios()) {
            return;
        }

        let inpNomeComp = document.getElementById("nomecomp");
        let inpCpf      = document.getElementById("cpf");
        let inpDataNasc = document.getElementById("datanasc");
        let inpFone     = document.getElementById("fone");
        let inpEmail    = document.getElementById("email");
        let inpSenha    = document.getElementById("senha");

        let user = {};

        user.nome           = inpNomeComp.value;
        user.cpf            = inpCpf.value;
        user.telefone       = inpFone.value;
        user.email          = inpEmail.value;
        user.senha          = inpSenha.value;
        user.dataNascimento = inpDataNasc.value;
        user.administrador  = false;
        user.endereco       = null;
        user.cartao         = null;

        api.put(`/Usuario`, user);
        alert('cadastro efetuado')
    }

    possuiCamposVazios = () => {
        let campos = document.querySelectorAll('form input');

        for(let i = 0; i < campos.length; i++) {
            if(!campos[i].value) {
                alert("Algum campo se encontra em branco");
                return true;
            }
        }

        return false;
    }

    render() {
        const { username } = this.state;

        return (
            <div className="cadastro-main-container">
                <header>
                    <strong>Crie sua conta</strong><br/>
                    <span>Os campos com * são obrigatórios</span>
                </header>
                <hr/>
                <div className="cadastro-container">
                    <form>
                        <input
                            id="nomecomp"
                            placeholder="Nome Completo"
                        />
                        <input
                            id="cpf"
                            placeholder="CPF"
                        />
                        <input
                            id="datanasc"
                            placeholder="Data de nascimento"
                        />
                        <input
                            id="fone"
                            placeholder="Fone"
                        />
                        <input
                            id="email"
                            placeholder="E-mail"
                        />
                        <input
                            placeholder="Confirmar e-mail"
                        />
                        <input
                            id="senha"
                            placeholder="Senha"
                        />
                        <input
                            placeholder="Confirmar senha"
                        />
                        <button type="button" onClick={this.cadastrar}>Criar Cadastro</button>
                    </form>
                </div>
            </div>
        );
    };

}
