import React, { Component }  from 'react';
import Routes from './routes';

import './css/cssStyle.css';

import Header              from './components/header';
import { ContextoUsuario } from './components/Session';

class App extends Component {

  state = {
    usuarioAutenticado : {},
    pesquisar          : false
  }

  usuarioLogin = () => {
    this.setState({
        usuarioAutenticado : {
          id : '3'
        }
    });
  }

  pesquisarAtivo = () => {
    this.setState({
      pesquisar : true
    });
  }

  render() {

    const { usuarioLogin, pesquisarAtivo } = this;

    const value = {
        ...this.state,
        usuarioLogin,
        pesquisarAtivo
    }

    return(
        <ContextoUsuario.Provider value={value}>
          <ContextoUsuario.Consumer> 
            {
              ({ 
                usuarioLogin, 
                usuarioAutenticado, 
                pesquisar, 
                pesquisarAtivo 
              }) => (
                <div className="App">
                  <Header {...{ 
                    usuarioAutenticado, 
                    pesquisarAtivo 
                  }}  />

                  <Routes {...{ 
                    usuarioLogin, 
                    usuarioAutenticado, 
                    pesquisar, 
                    pesquisarAtivo 
                    }}  />
                </div>
              )
            }
          </ContextoUsuario.Consumer>
        </ContextoUsuario.Provider>
    );
  }

}

export default App;
